'use client';

import * as React from 'react';
import { Button } from '../button/button';

export type AnouncementProps = {
  title: string;
  description?: string;
  actionLabel?: string;
  onAction?: () => void;
  closable?: boolean;
};

export default function Anouncement({
  title,
  description,
  actionLabel = 'Action',
  onAction,
  closable = false,
}: AnouncementProps) {
  const [visible, setVisible] = React.useState(true);

  if (!visible) return null;

  return (
    <div className="flex w-full items-center justify-between gap-4 rounded-md border bg-muted px-4 py-3 text-sm">
      <div>
        <p className="font-medium">{title}</p>
        {description && <p className="text-xs opacity-80">{description}</p>}
      </div>

      <div className="flex items-center gap-2">
        {onAction && (
          <Button size="sm" onClick={onAction}>
            {actionLabel}
          </Button>
        )}

        {closable && (
          <Button size="sm" variant="outline" onClick={() => setVisible(false)}>
            Close
          </Button>
        )}
      </div>
    </div>
  );
}
